 //validate password
 export const onValidatePass = async (e, inputPassword,confirmPassword) => {
    e.preventDefault();
    if (confirmPassword === '') {
        swalWarnPasswordInput();
    }

    else if (onCheckPassword(form.upass) === false) {
        swalWarnPasswordForm();
    }

    else if (inputPassword !== confirmPassword) {
        swalWarnNotEqualPassword();
    }

    else if (inputPassword === confirmPassword) {
        swalSuccessEqualPassword();
        setIsChecked(true);
    }
}
