import React, { useRef } from 'react';
import "slick-carousel/slick/slick-theme.css";
import "slick-carousel/slick/slick.css";
import "./Carousel.css";
import BestList from './BestList';
import 'animate.css'; //이거 없으면 animate 안됨.

const HomePage = () => {
  const ImageRef = useRef('bb');

  var animation = function () {
    let items;
    let winH;

    var initModule = function () {
      items = ImageRef.current.window;
      winH = window.innerHeight;
      _addEventHandlers();
    }

    var _addEventHandlers = function () {
      window.addEventListener("scroll", _checkPosition);
      window.addEventListener("load", _checkPosition);
      window.addEventListener("resize", initModule);
    };

    console.log(items);

    var _checkPosition = function () {
      for (var i = 0; i < items.length; i++) {
        var posFromTop = items[i].getBoundingClientRect().top;
        if (winH > posFromTop) {
          items[i].classList.add("animate__backInLeft");
        }
      }
    }

    return {
      init: initModule
    }
  }

  animation().init();

  return (
    <div className='sql'>

      <img
        sizes='100vw'
        className="d-block w-100"
        src="/image/image17.png"
      />

      <div className='img5'>
        <img
          size='10px'
          className="d-block w-100"
          src="/image/image16.jpg"
        />
      </div>

      <div ref={ImageRef}
        className='animate__animated'>
        <img
          size='10px'
          className="d-block w-100"
          src="/image/image19.jpg"
        />
      </div>

      <hr />
      <div style={{ marginTop: 400 }}>
        <h3 className='animate__animated animate__swing animate__repeat-2'>인기상품</h3>
        <BestList />
      </div>
    </div>





  )
}

export default HomePage
