    return (
        <>
        
                <>
                    <div>
                        <p style={{ fontSize: 20 }}>{who[0]}</p>
                        <img className='icon' src={uprofile} />
                        <p >{date}</p>
                    </div>
                    <div className="textbox">
                        <p style={{ textAlign: 'left' }}>
                            {text}
                        </p>
                        {who[0] === loginUser.uid && <Button className='mt-3' onClick={() => onDelete(id)}>삭제</Button>}
                    </div>
                </>


        </>


    )
}

