import React, { useEffect } from 'react';

const MyChatList = () => {
    const getMessages = () => {
        const q = query(
            collection(db, `${pwriter_id}`),
            orderBy('date', 'asc'),
            limit(100)
        );

        onSnapshot(q, (snapshot) => {
            let rows = [];
            snapshot.forEach((doc) => {
                rows.push({
                    uid: doc.data().uid,
                });
            });
            setMessages(rows);
        });
    }

    useEffect(() => {
        getMessages();
    }, [])

    return (
        <div style={{ marginTop: 100 }}>
            list
        </div>
    )
}

export default MyChatList
