import axios from 'axios';
import qs from 'qs';
import React, { useEffect, useState } from 'react';
import { Button, Spinner, Table } from 'react-bootstrap';
import Pagination from 'react-js-pagination';
import { useLocation, useNavigate } from 'react-router-dom';

const MyBuyList = () => {
  const location=useLocation();
  const navigate=useNavigate();
  const search = qs.parse(location.search, { ignoreQueryPrefix: true });
  const [buyList, setBuyList] = useState(['aaa']);
  const [total, setTotal] = useState(1);
  const [loading, setLoading] = useState(false);
  const page = parseInt(search.page) || 1;
  const buyer = search.buyer;

  const fetchBuyList = async () => {
    setLoading(true);
    const result = await axios.get(`/api/tradeinfo/{buyer}?page=${page}`)
    setBuyList(prev=>result.data.list);
    setTotal(result.data.total);
    setLoading(false);
  }

  useEffect(() => {
    fetchBuyList();
  }, [page])

  if (loading) return (
    <Spinner animation="border" variant="primary"
      style={{ width: '20rem', height: '20rem', marginTop: '220px' }} />
  )

  const onPageChange=(e)=>{
    navigate(`/my/buy?page=${e}`)
    window.scrollTo({
        top:0,
        left:150,
        behavior: 'smooth'
    })
}



  return (
    <div>
      <button style={{ marginTop: 30, float: 'left' }} onClick={() => navigate(`/my/buychart?buyer=${buyer}`)}>구매내역 차트로 확인</button>
      <Table striped className='mt-5'>
        <thead>
          <tr >
            <th>판매가격</th>
            <th>판매일자</th>
            <th>후기쓰기</th>
          </tr>
        </thead>
        <tbody>
          {buyList.map(list =>
            <>
              <tr key={list.paycode}>
                <td>{list.payprice}</td>
                <td>{list.regDate}</td>
                {(list.buyercondition === 0) ?
                  /* buyer=seller, seller=buyer로 거꾸로 바꿔주기만 하면 seller가 buyer에 관한 리뷰를 쓸 수 있음. */
                  <td><Button onClick={() => navigate(`/my/review/insert/${list.paycode}?buyer=${list.buyer}&seller=${list.seller}&pcode=${list.pcode}`)}>
                    후기쓰러가기</Button></td>
                  :
                  <td>후기 작성완료</td>
                }
              </tr>
            </>
          )}
        </tbody>
      </Table>

      {buyList.length !== 0 ? <div style={{ marginLeft: 605 }}>
        <Pagination
          activePage={page}
          itemsCountPerPage={6}
          totalItemsCount={total}
          pageRangeDisplayed={10}
          prevPageText={"‹"}
          nextPageText={"›"}
          onChange={(e) => onPageChange(e)}
        /> </div>
        : <div style={{ marginTop: 200 }}>
          <h1 style={{ fontSize: 60, color: 'red', marginBottom: 200 }}>해당 검색 결과가 존재하지 않습니다.</h1>
        </div>
      }
    </div >


  )
}

export default MyBuyList
