import Swal from 'sweetalert2';

export const swalRequirePassword = () => {
    Swal.fire({
        text: "비밀번호를 입력하세요",
        icon: 'warning',
        confirmButtonColor: '#3085d6',
    })
}

export const swalComplyPasswordForm = () => {
    Swal.fire({
        text: "비밀번호 양식을 준수해주세요",
        icon: 'warning',
        confirmButtonColor: '#3085d6',
    })

}

export const swalAlertNotEqualPassword = () => {
    Swal.fire({
        text: "비밀번호가 서로 일치하지 않습니다!",
        icon: 'warning',
        confirmButtonColor: '#3085d6',
    })
}
