import Swal from 'sweetalert2';

export const swalBoardDelete = () => {
    Swal.fire({
        text: "해당 상품은 판매되었거나 삭제된 상품입니다",
        icon: 'error',
        confirmButtonColor: '#3085d6',
    })
}

export const swalQueryBoardUpdate = () => {
    Swal.fire({
        text: "정말로 수정하시겠습니까?",
        icon: 'info',
        showCancelButton: true,
        confirmButtonColor: '#3085d6',
        cancelButtonColor: '#d33',
        confirmButtonText: '수정',
        cancelButtonText: '취소'
    })
}

export const swalError = () => {
    Swal.fire({
        text: "예상치 못한 오류가 발생하였습니다",
        icon: 'error',
        confirmButtonColor: '#3085d6',
    })
}

export const swalSuccessBoardUpdate = () => {
    Swal.fire({
        text: "수정을 완료하였습니다!",
        icon: 'success',
        confirmButtonColor: '#3085d6',
        cancelButtonColor: '#d33',
    })
}
